import React, { useEffect, useState } from "react";
import {
  View,
  Button,
  Text,
  StyleSheet,
  TouchableOpacity,
  Image,
} from "react-native";
import axios from "axios";

import { SafeAreaView } from "react-native-safe-area-context";

const DashScreen = ({ navigation }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [notice, setNotice] = useState([]);
  const [libs, setLibs] = useState([]);
  const [facility, setFacility] = useState([]);
  const [bookLoan, setBookLoan] = useState([]);

  /*
      console.log('\n', '\n', '\n', '\n', '\n', '\n', '\n', '\n');
      console.log(response);
  */

  useEffect(() => {
    setIsLoading(true);
    const count = 0;
    const notice_num = 22; //추후 숫자 바꿔야 함

    //공지사항 제목 불러오기
    axios
      .get(`https://library-2022.herokuapp.com/notice/detail?id=${notice_num}`)
      .then(function (response) {
        console.log("\n", "\n", "\n", "\n", "\n", "\n", "\n", "\n");
        setNotice(response.notice);
        console.log(response.data.noticeDetail[0].title);
      })
      .catch(console.error)
      .finally(() => setIsLoading(false));

    //도서관 리스트 조회
    axios
      .get("https://library-2022.herokuapp.com/book/category")
      .then(function (response) {
        setLibs(response.libs);
        //console.log(response);
      })
      .catch(console.error)
      .finally(() => setIsLoading(false));

    //시설 예약 내역 (시설 예약 조회)
    axios
      .get(
        "https://library-2022.herokuapp.com/facility/reserve/state?user_id=1"
      )
      .then(function (response) {
        setFacility(response.facility);
      })
      .catch(console.error)
      .finally(() => setIsLoading(false));

    //자료 대출 내역 (대출현황 조회)
    axios
      .get("https://library-2022.herokuapp.com/book/loan/state?user_id=1")
      .then(function (response) {
        setBookLoan(response.bookLoan);
        console.log(response);
      })
      .catch(console.error)
      .finally(() => setIsLoading(false));
  }, []);

  const today = new Date();
  var year = today.getFullYear();
  const month = ("0" + (today.getMonth() + 1)).slice(-2);
  var day = ("0" + today.getDate()).slice(-2);

  return (
    <SafeAreaView>
      <View>
        <Text>{"공지 1 " + `${notice}`}</Text>
        <Text>{"공지 2 " + `${notice}`}</Text>
      </View>

      <View>
        <Text> </Text>
        <Text> </Text>
      </View>

      <View>
        <Text>
          {"개관시간 " + `${year}` + "/" + `${month}` + "/" + `${day}` + "요일"}
        </Text>
      </View>

      <View>
        <Text>도서관 이름, 오픈 시간 </Text>
      </View>

      <View>
        <Text> </Text>
        <Text> </Text>
      </View>
    </SafeAreaView>
  );
};

export default DashScreen;

const styles = StyleSheet.create({});
